import React, { Component, useState } from "react";
import "./index.css";
import InfiniteCalendar from 'react-infinite-calendar';
import 'react-infinite-calendar/styles.css';



export default class KanbanBoard extends Component {
  constructor() {
    super();
    // Each task is uniquely identified by its name. 
    // Therefore, when you perform any operation on tasks, make sure you pick tasks by names (primary key) instead of any kind of index or any other attribute.
    this.state = {

      tasks: [],
      inputValue: '',
      courseInput: '',
      dateInput: '',
      calendarDate: new Date(),



    };
    this.stagesNames = ['Backlog', 'To Do', 'Ongoing','Review'];
    this.handleChange = this.handleChange.bind(this);
    this.onFormSubmit = this.onFormSubmit.bind(this);

    // useEffect(() => {
    //   getMerchant();
    //    }, []);





  //   var myData =  require('./tasks.json');
  //     for (let i =0; i < Object.keys(myData.all_tasks).length; i++) {
  //       var values = [];
  //       for (const [key, value] of Object.entries(myData.all_tasks[i])) {
  //           values.push(value);
  //       }           
  //         this.state.tasks.push({
  //           name: values[0],
  //           course: values[1],
  //           chosenDate: values[2],
  //           stage: values[3],

  //       }); 

  // };

  }

  getMerchant = () => {
    fetch('http://localhost:3001')
      .then(response => {
        // alert(response.text());
        return response.text();
      })
      .then(data => { 
        const obj = JSON.parse(data);
        for (let i =0; i < Object.keys(obj).length; i++) {

          this.state.tasks.push({
            name: obj[i].task_name,
            course: obj[i].course,
            chosenDate: obj[i].due_date,
            stage: 0,
        }); 

        };
        alert(obj[0].task_name);
      });
  }



  createMerchant(name, course, due_date) {
    // let name = prompt('Enter task name');
    // let course = prompt('Enter task course');
    // let due_date = prompt('Enter task due date');
    fetch('http://localhost:3001/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({name, course, due_date}),
    })
      .then(response => {
        return response.text();
      })
      .then(data => {
        alert(data);
        this.getMerchant();
      });
  }

    deleteMerchant() {
    let id = prompt('Enter task id');
    fetch(`http://localhost:3001/tasks/${id}`, {
      method: 'DELETE',
    })
      .then(response => {
        return response.text();
      })
      .then(data => {
        alert(data);
        this.getMerchant();
      });
  }

  handleChange(date) {
    this.setState({
      calendarDate: date.toLocaleDateString("sv-SE"),
      dateInput: date.toLocaleDateString("sv-SE").toString()
    })

  }

  onFormSubmit(e) {
    e.preventDefault();
    console.log(this.state.chosenDate)
  }

  onInputChange = (e) => {
    this.setState({
      inputValue: e.target.value
    })
  }

  onCourseInputChange = (e) => {
    this.setState({
      courseInput: e.target.value
    })
  }

  // onDateInputChange = (e) => {
  //   this.setState({
  //     dateInput: e.target.value
  //   })
  // }
  //  createMerchant = () => {
  //   let name = this.state.inputValue;
  //   let course = this.state.courseInput;
  //   let due_date = this.state.dateInput;
  //   fetch('http://localhost:3001/tasks', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({name, course, due_date}),
  //   })
  //     .then(response => {
  //       return response.text();
  //     })

  // }
  addTask = () => {
    if (this.state.inputValue) {
      const _tasks = this.state.tasks;
      this.createMerchant(this.state.inputValue, this.state.courseInput, this.state.dateInput);
      _tasks.push({ name: this.state.inputValue, course: this.state.courseInput, chosenDate: this.state.dateInput,  stage: 0 });


      this.setState({ tasks: _tasks, inputValue:'' });
      this.setState({ tasks: _tasks, courseInput:'' });
      this.setState({ tasks: _tasks, dateInput:'' });
    }

  }

  enterAddTask = (e) => {
    if (e.key === 'Enter') {
      this.addTask()
    }
  }

  back = (name) => {
    let _tasks = this.state.tasks;
    _tasks = _tasks.map((task) => {
      if (task.name == name) return { name: task.name, course: task.course, chosenDate: task.chosenDate, stage: task.stage == 0 ? 0 : task.stage - 1 };
      else return task;
    });

    this.setState({ tasks: _tasks });
  }

  forward = (name) => {
    let _tasks = this.state.tasks;
    _tasks = _tasks.map((task) => {
      if (task.name == name) return { name: task.name, course: task.course,chosenDate: task.chosenDate, stage: task.stage == 3 ? 3 : task.stage + 1 };
      else return task;
    });

    this.setState({ tasks: _tasks });
  }

  remove = (name) => {
    let _tasks = this.state.tasks;
    _tasks = _tasks.filter(e => e.name != name);
    this.setState({ tasks: _tasks });
  }

  // deleteTask = () => {
  //   ;


  // }


  render() {
    const { tasks } = this.state;

    let stagesTasks = [];
    for (let i = 0; i < this.stagesNames.length; i++) {
      stagesTasks.push([]);
    }
    for (let task of tasks) {
      const stageId = task.stage;
      stagesTasks[stageId].push(task);
    }

    return (
      <div className="mt-20 layout-column justify-content-center align-items-center">
        <section className="deadlineCal">
          <InfiniteCalendar

            selected={ this.state.calendarDate }
            onSelect={ this.handleChange }
            locale={{
            weekStartsOn: 1}}
            displayOptions={{
            layout: 'landscape'
            }}
            width={800}
            height={200}
          />
         </section>
         <section className="mt-50 layout-row align-items-center justify-content-center">
          <input value={this.state.inputValue} onChange={this.onInputChange} onKeyDown={this.enterAddTask} id="create-task-input" type="text" className="large" placeholder="New task name" data-testid="create-task-input" />
          <input value={this.state.courseInput} onChange={this.onCourseInputChange} onKeyDown={this.enterAddTask} id="create-task-input" type="text" className="large" placeholder="Course code" data-testid="create-task-input" />
          <input value={this.state.dateInput} onKeyDown={this.enterAddTask} id="create-task-input" type="text" className="large" placeholder="Deadline date" data-testid="create-task-input" />


          <button onClick={this.addTask} type="submit" className="ml-30" data-testid="create-task-button">Create task</button>
          <button  onClick={this.getMerchant} type="submit" className="save-progress">Delete a task </button>
        </section>
        <div className="mt-50 layout-row">
          {stagesTasks.map((tasks, i) => {
            return (
              <div className="card outlined ml-20 mt-0" key={`${i}`}>
                <div className="card-text">
                  <h4>{this.stagesNames[i]}</h4>
                  <ul className="styled mt-50" data-testid={`stage-${i}`}>
                    {tasks.map((task, index) => {
                      return <li className="slide-up-fade-in" key={`${i}${index}`}>
                        <div className="layout-column justify-content-between align-items-center">
                        <section className="mt-50 layout-column align-items-center justify-content-center ">
                          <span data-testid={`${task.name.split(' ').join('-')}-name`}>{task.name}</span>
                          <section className="mt-50 layout-row align-items-center justify-content-center ">
                          <span data-testid={`${task.course.split(' ').join('-')}-course`} className="taskIcon">{task.course}</span>
                          <span data-testid={`${task.chosenDate.split(' ').join('-')}-date`} className="taskIcon2">{task.chosenDate}</span>
                          </section>
                        </section>
                          <div className="icons">
                            <button onClick={() => this.back(task.name)} className="icon-only small mx-2" data-testid={`${task.name.split(' ').join('-')}-back`} disabled={task.stage == 0}>
                              <i className="material-icons">arrow_back</i>
                            </button>
                            <button onClick={() => this.forward(task.name)} className="icon-only small mx-2" data-testid={`${task.name.split(' ').join('-')}-forward`} disabled={task.stage == 3}>
                              <i className="material-icons">arrow_forward</i>
                            </button>
                            <button onClick={() => this.remove(task.name)} className="icon-only medium mx-2" data-testid={`${task.name.split(' ').join('-')}-delete`}>
                              <i className="material-icons">checkmark</i>
                            </button>
                          </div>
                        </div>
                      </li>
                    })}
                  </ul>
                </div>
              </div>
            )
          })}
        </div>

      </div>
    );
  }
}
