import React,{Component }  from 'react';
import './styles.css';
import { Button } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css'

export default class WeatherCard extends Component {
  constructor() {
    super();

  }


getData = () => {
  return fetch('https://opendata-download-metfcst.smhi.se/api/category/pmp3g/version/2/geotype/point/lon/18.1489/lat/57.3081/data.json')
  .then(response => {
    // alert(response.tid);
    return response.JSON();
  })

}
refresh = () => {
  const data = this.getData();

  window.location.reload();

}


render() {
  console.log("hello");

  return (
    <div className="mt-20 layout-column justify-content-center align-items-center">
  <div className="main">
      <div className="top">
        <p className="header">Weather in Stockholm today</p>
        <Button className="button" inverted color='blue' circular icon='refresh' onClick={this.refresh} />
      </div>
      <div className="flex">
        <p className="day"><span>Hello</span></p>
        <p className="description">Hello</p>
      </div>

      <div className="flex">
        <p className="temp">Temprature:</p>
        <p className="temp">Humidity:</p>
      </div>

      <div className="flex">
        <p className="sunrise-sunset">Sunrise:</p>
      </div>

  </div>
  </div>

  )
  }
}
